import gql from 'graphql-tag';

const SINGLE_ITEM_QUERY = gql`
    query {
        Product(where: { id: ""}) {
            name
            price
            description
        }
    }

`

export default function SingleProduct({ query }) {
    const { data, loading, error } = useQuery(SINGLE_ITEM_QUERY)


    return (
        <div>
            Single Product {query.id}
        </div>
    )
}
