import gql from 'graphql-tag';

const CREATE_PRODUCT_MUTATION = gql`
    mutation CREATE_PRODUCT_MUTATION(
        #which variables are getting passed in and the type
        $name: String!
        $description: String!
        $price: Int!
        $image: Upload
    ){
        createProduct(data:{
            name: $name,
            description: $description,
            price: $price,
            status: "AVAILABLE",
            photo: {
                create: {
                    image: $image,
                    altText: $name
                }
            }
        }) {
            id
            price
            description
            name
        }
    }
`

